mapper.select:
select no,bno,name
from category_table
where no = #{value}

select no,city,local
from location_table
where city = #{value}

select no,city,local
from location_table
where city = #{city} and local = #{local}

select no,city,local
from location_table
where no = #{value}

mapper.update:
update product_table
set hits = hits+1
where no = #{value}

mapper.select:
select sum(score) as "totalScore" ,count(score) as "rowCount"
from product_review
where productno = #{value}

