mapper.select:
select
	id
from
	user_table
where
	name = #{name}
and
	birth = #{birth}
and
	phone = #{phone}

select
	*
from
	user_table
where
	id = #{id}
and
	question = #{question}
and
	answer = #{answer}

mapper.update:
update user_table 
set password = #{password}
where id = #{id}
